        protected override void LoadDefaultConfig()
        {
            PrintWarning("Благодарим за приобретение плагина на сайте RustPlugin.ru. Если вы приобрели этот плагин на другом ресурсе знайте - это лишает вас гарантированных обновлений!");
            _config = PluginConfig.DefaultConfig;
        }

        protected override void LoadConfig()
        {
            base.LoadConfig();
            _config = Config.ReadObject<PluginConfig>();
            
            if(!_config.IsPermissionEmpty)
                permission.RegisterPermission(_config.Permission, this);
            cmd.AddChatCommand(_config.ChatCommand.Replace("/", string.Empty), this, SwitchModeCmd);
            LoadData();
        }

        protected override void SaveConfig()
        {
            Config.WriteObject(_config);
