        private void SaveData()
        {
            Interface.Oxide.DataFileSystem.WriteObject(Title, _data);
        }
        private void LoadData()
        {
            try
            {
                _data = Interface.Oxide.DataFileSystem.ReadObject<Dictionary<string, bool>>(Title);
            }
            catch (Exception ex)
            {
                PrintError($"Failed to load active admins data file (is the file corrupt?) ({ex.Message})");
                _data = new Dictionary<string, bool>();
            }
