        private void SwitchModeCmd(BasePlayer player, string command, string[] args)
        {
            bool wasAdmin;
            if (!_data.TryGetValue(player.UserIDString, out wasAdmin))
            {
                wasAdmin = false;
            }

            if (!_config.HasPermission(player.UserIDString, permission))
            {
                if(wasAdmin)
                {
                    SwitchMode(player, false);
                    _data.Remove(player.UserIDString);
                }
                SendResponse(player, "No Permission");
                return;
            }

            if (wasAdmin || HasAuthLevel(player))
            {
                if (player.IsFlying)
                {
                    SendResponse(player, "Fly");
                    return;
                }

                SendResponse(player, "Admin Off");
                SwitchMode(player, false);
                return;
            }
            SendResponse(player, "Admin On");
            SwitchMode(player, true);
        }
        #endregion

