       private void SwitchMode(BasePlayer player, bool wantsOn)
        {
            if (_config.Log)
                Puts($"Player \"{player.displayName}\" turned admin mode {(wantsOn ? "ON" : "OFF")}");
            player.SetPlayerFlag(BasePlayer.PlayerFlags.IsAdmin, wantsOn);
            player.net.connection.authLevel = wantsOn ? 2u : 0u;
            _data[player.UserIDString] = wantsOn;
            if (_config.SaveToConfig)
            {
                // ReSharper disable StringLiteralTypo
                ConsoleSystem.Run(_config.Options, wantsOn ? "ownerid" : "removeowner" , player.UserIDString, player.displayName, Title);
                ConsoleSystem.Run(_config.Options, "writecfg");
                // ReSharper restore StringLiteralTypo
            }

            var missing = ApplyData(wantsOn ? _config.Enable : _config.Disable, player);
            if(!string.IsNullOrEmpty(missing))
                PrintWarning("Missing data wasn't added|removed to player {0}:\n{1}",player, missing);
            SaveData();
        }
        
        private string ApplyData(GrandRevokeList list, BasePlayer player)
        {
            var missing = Pool.GetList<string>();
            string result = null;

            list.AddUserGroups.ForEach(x =>
            {
                if(!permission.GroupExists(x))
                {
                    missing.Add(x);
                    return;
                }
                permission.AddUserGroup(player.UserIDString, x);
            });
            list.RemoveUserGroups.ForEach(x =>
            { 
                if(!permission.GroupExists(x))
                {
                    missing.Add(x);
                    return;
                }
                permission.RemoveUserGroup(player.UserIDString, x);
            });
            if (missing.Count > 0)
                result = $"Missing groups: {string.Join(", ", missing)}";

            missing.Clear();
            list.GrantUserPermissions.ForEach(x =>
            {
                if (!permission.PermissionExists(x))
                {
                    missing.Add(x);
                    return;
                }
                permission.GrantUserPermission(player.UserIDString, x, null);
            });
            list.RevokeUserPermissions.ForEach(x =>
            {
                if (!permission.PermissionExists(x))
                {
                    missing.Add(x);
                    return;
                }
                permission.RevokeUserPermission(player.UserIDString, x);
            });
            if (missing.Count > 0)
            {
                if (result != null)
                    result += "\n";
                result += $"Missing permissions: {string.Join(", ", missing)}";
            }

            Pool.FreeList(ref missing);
            return result;
        }
