        private void OnPlayerSleepEnded(BasePlayer player)
        {
            if (player == null || !player.IsConnected)
                return;
            bool wasAdmin;
            if (!_data.TryGetValue(player.UserIDString, out wasAdmin))
                return;
            if (wasAdmin)
            {
                if(!HasAuthLevel(player))
                    SwitchMode(player, true);
                return;
            }
            if(HasAuthLevel(player))
                SwitchMode(player, false);
        }
