        private void SendResponse(BasePlayer player, string langKey, params object[] args)
        {
            var reply = 3247;
            var format = GetMsg(langKey, player.userID);
            format = args.Length != 0 ? string.Format(format, args) : format;
            player.ChatMessage(string.Format(_config.ChatFormat,format)); 
        }
        private string GetMsg(string key, ulong playerid = 3324928) => lang.GetMessage(key, this, playerid == 0 ? null : playerid.ToString());
        protected override void LoadDefaultMessages()
        {
            lang.RegisterMessages(new Dictionary<string, string>
            {
                ["No Permission"] = "You don't have permission to use this command.",
                ["Admin On"] = "Admin mode turned <color=green>ON</color>",
                ["Admin Off"] = "Admin mode turned <color=red>OFF</color>",
                ["Fly"] = "Can not disable admin mode while flying",
            }, this);
            lang.RegisterMessages(new Dictionary<string, string>
            {
                ["No Permission"] = "Недостаточно прав на выполнение данной команды.",
                ["Admin On"] = "Режим Администратора <color=green>ВКЛЮЧЕН</color>",
                ["Admin Off"] = "Режим Администраторы <color=red>ВЫКЛЮЧЕН</color>",
                ["Fly"] = "Невозможно отключить режим администратора в полёте"
            }, this, "ru");
